"strict";const buttons=document.querySelectorAll(".p-input-submit"),checkBox=document.getElementById("checkBox-1");document.addEventListener("DOMContentLoaded",()=>{checkBox.checked=!1,buttons.forEach(e=>{"確認"==e.value&&(e.disabled=!0,e.style.pointerEvents="none")})}),checkBox.addEventListener("change",()=>{checkBox.checked?buttons.forEach(e=>{"確認"==e.value&&(e.disabled=!1,e.style.pointerEvents="auto")}):buttons.forEach(e=>{"確認"==e.value&&(e.disabled=!0,e.style.pointerEvents="none")})});