document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("details").forEach(e=>{const t=e.querySelector("summary"),i=e.querySelector("summary + .wp-block-group");t&&i&&t.addEventListener("click",t=>{if(t.preventDefault(),null!=e.getAttribute("open")){i.animate(closingAnimation(i),animTiming).onfinish=(()=>{e.removeAttribute("open")})}else e.setAttribute("open","true"),i.animate(openingAnimation(i),animTiming)})})});const animTiming={duration:500,easing:"ease-in-out"},closingAnimation=e=>[{height:`${e.offsetHeight}px`,opacity:1},{height:0,opacity:0}],openingAnimation=e=>[{height:0,opacity:0},{height:`${e.offsetHeight}px`,opacity:1}];